library(Biostrings)
library(msa)
gfp_segment <- Biostrings::DNAStringSet(c(
    "",
    "",
    "",
    "",
    "",
    "",
    "",
    "",
    "CTACTTGTACAGCTCGTCCATGCCGAGAGTGATCCCGGCGGCGGTCACGAACTCCAACAGGACCAGCTGTGATCACTTCT",
    "CTACTTGTACAGCTCGTCCATGCCGAGAGTGATCCCGGCGGCGGTCACGAACTCCAGCAGGACCATGTGATCCGCGCTTCT",
    "CTACTTGTACAGCTCGTCCATGCCGAGAGTGATCCCGGCGGCGGGTCTGAACTCCGCGGGACCATGTGATCGCTTCTCGTT",
    "CTACTTGTACAACCTCGTCCATGCCGAGAAGCTGATCCCGGCGGCGGTGCGAACTCCAGCGGGACCATGTGATCCTGTAA"
))
gapOpening <- 0
gapExtension <- 1

alignments <- msa::msa(gfp_segment,
                       method = 'ClustalW',
                       gapOpening = gapOpening,
                       gapExtension = gapExtension)

msa::msaPrettyPrint(alignments, output="pdf", showNames="none",
                    showLogo="none", askForOverwrite=FALSE, verbose=FALSE)

consensus <- msa::msaConsensusSequence(alignments, ignoreGaps = FALSE)
gsub('[[:punct:]]', '', consensus)

